import PySimpleGUI as sg
from PIL import ImageGrab


def save_element_as_file(element, filename):
    """
    Saves any element as an image file.  Element needs to have an underlyiong Widget available (almost if not all of them do)
    :param element: The element to save
    :param filename: The filename to save to. The extension of the filename determines the format (jpg, png, gif, ?)
    """
    widget = element.Widget
    box = (widget.winfo_rootx(), widget.winfo_rooty(), widget.winfo_rootx() + widget.winfo_width(), widget.winfo_rooty() + widget.winfo_height())
    grab = ImageGrab.grab(bbox=box)
    grab.save(filename)


def main():
    sg.theme('Dark Grey 13')

    col_left = [
        [sg.T("Select input pdf file:")],
        [sg.InputText(disabled=True), sg.FileBrowse()],
        [sg.HSeparator()],
        [sg.Text("Scanner effects:")],
        [sg.Checkbox("Random blur"), sg.Slider((0, 1), resolution=0.01, orientation="horizontal")],
        [sg.Checkbox("Random rotate"), sg.Slider((0, 1), resolution=0.01, orientation="horizontal")],
        [sg.Checkbox("Gamma"), sg.Slider((0, 1), resolution=0.01, orientation="horizontal")],
        [sg.Checkbox("Grayscale")],
        [sg.HSeparator()],
        [sg.Text("Place signature:")],
        [sg.DropDown(["Signature1.png", "Signature2.png"])],
        [sg.Radio("Place", key="-PLACE-", group_id=0)],
        [sg.Radio("Move", key="-MOVE-", group_id=0)],
        [sg.Radio("Remove", key="-REMOVE-", group_id=0)],
        [sg.HSeparator()],
        [sg.Radio("Preview", key="-PREVIEW-", group_id=0)],
    ]

    col_right = [
        [
            sg.Graph(
                canvas_size=(400, 400),
                graph_bottom_left=(0, 0),
                graph_top_right=(800, 800),
                key="-GRAPH-",
                enable_events=True,
                background_color='lightblue',
                drag_submits=True,
                right_click_menu=[[], ['Erase item', ]]
            )
        ],
        [sg.Button("<", key="-PREVIOUS-"), sg.Text("Page 1 / 10", key="-CURRENT-PAGE-"), sg.Button(">", key="-NEXT-")],
        [sg.Button("Save pdf...", key="-SAVE-")]
    ]

    layout = [
        [sg.Col(col_left, key="-COL-LEFT-"), sg.Col(col_right, key="-COL-RIGHT-")],
        [sg.Text(key="-INFO-", size=(60, 1))]
    ]

    window = sg.Window("FalsiSignPy", layout, finalize=True)

    graph = window["-GRAPH-"]  # type: sg.Graph

    dragging = False
    drag_figures = []
    lastxy = (None, None)
    start_point = end_point = floating_signature = None

    while True:
        event, values = window.read()
        print(event, values)
        if event == sg.WIN_CLOSED:
            break  # exit

        if event == '-MOVE-':
            graph.set_cursor(cursor='fleur')
        elif not event.startswith('-GRAPH-'):
            graph.set_cursor(cursor='left_ptr')

        if event == "-GRAPH-":  # if there's a "Graph" event, then it's a mouse
            x, y = values["-GRAPH-"]
            if not dragging:
                start_point = (x, y)
                dragging = True
                drag_figures = graph.get_figures_at_location((x, y))
                lastxy = x, y
            else:
                end_point = (x, y)
            if floating_signature:
                graph.delete_figure(floating_signature)
            delta_x, delta_y = x - lastxy[0], y - lastxy[1]
            lastxy = x, y
            if None not in (start_point, end_point):
                if values['-MOVE-']:
                    for fig in drag_figures:
                        graph.move_figure(fig, delta_x, delta_y)
                        graph.update()
                elif values['-PLACE-']:
                    floating_signature = graph.draw_image(data=logo200, location=start_point)
                elif values['-REMOVE-']:
                    for figure in drag_figures:
                        graph.delete_figure(figure)
            window["-INFO-"].update(value=f"mouse {values['-GRAPH-']}")
        elif event.endswith("+UP"):  # The drawing has ended because mouse up
            window["-INFO-"].update(value=f"grabbed rectangle from {start_point} to {end_point}")
            start_point, end_point = None, None
            dragging = False
            floating_signature = None
        elif event == "-SAVE-":
            filename = sg.popup_get_file("Save pdf...", save_as=True)
            if filename is not None:
                save_element_as_file(window["-GRAPH-"], filename)

    window.close()


logo200 = b''

main()
